        subroutine qug_nInJqu_SquaredMEHel(res, flags)
        implicit none
        ResType res(HelDim(*))
        integer flags

#include "qug_nInJqu_vars.h"

        HelType amp
        ResType ampsq

* BEGIN ABBR_HEL
        call qug_nInJqu_abbr0h
* END ABBR_HEL

        ampsq = 0
* BEGIN FF_TREE
        Ctree(HelInd(1)) = 0

        call qug_nInJqu_born

        LOOP(Sfe6, 1,2,1)
        call qug_nInJqu_born_Sfe6
        ENDLOOP(Sfe6)
* END FF_TREE

* BEGIN M2_TREE
        amp = 0
        amp = amp + MatSUN(1,1)*Ctree(HelInd(1))
        ampsq = ampsq + Re(Conjugate(Ctree(HelInd(1)))*amp)
* END M2_TREE
        res(HelInd(1)) = ampsq

        ampsq = 0
        res(HelInd(2)) = ampsq
        end

